var showResources=function(){var e=window.location.hash;if(e){var o=document.querySelector(e),r=document.querySelector('[href="'+e+'"]'),s=Array.from(document.querySelectorAll(".resource-filter-item")),a=document.querySelector(".active");$(s).addClass("removed"),$(a).removeClass("active"),$(o).removeClass("removed"),$(r).addClass("active")}};window.addEventListener("load",showResources),window.addEventListener("hashchange",showResources);